<script lang="ts">
import tippy from 'tippy.js';
import { defineComponent, onMounted, Ref, ref } from 'vue';

export default defineComponent({
    name: "IonIcon",
    props: ['name', 'tooltip'],
    setup(props)
    {
        const icon: Ref<HTMLElement | null> = ref(null);

        onMounted(() =>
        {
            if (props.tooltip && icon.value)
            {
                tippy(icon.value, 
                {
					content: props.tooltip,
					inertia: true,
					animation: 'scale',
					theme: 'material',
                    delay: [1000, 0]
                });
            }
        });

        return {
            icon
        };
    }
})
</script>
